import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';
import { Container, Form, Button, Row, Col, Card, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const FORGOT_PASSWORD_MUTATION = gql`
  mutation ForgotPassword($email: String!) {
    forgotPassword(email: $email) {
      message
    }
  }
`;

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [forgotPassword] = useMutation(FORGOT_PASSWORD_MUTATION);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email) {
      setErrorMessage('Email is required');
      return;
    }

    try {
      const result = await forgotPassword({ variables: { email } });
      setSuccessMessage(result.data.forgotPassword.message);
      setErrorMessage('');
    } catch (e) {
      setErrorMessage(e.message);
      setSuccessMessage('');
      console.error('Error during forgot password:', e.message);
    }
  };

  return (
    <Container className="mt-5 mb-4">
      <Row className="justify-content-center">
        <Col md={6}>
          <Card className="shadow-lg">
            <Card.Body className="p-4">
              <h2 className="text-center mb-4">Forgot Password</h2>
              
              {successMessage && <Alert variant="success" className="text-center">{successMessage}</Alert>}
              {errorMessage && <Alert variant="danger" className="text-center">{errorMessage}</Alert>}
              <Form onSubmit={handleSubmit} className="form-forgot-password">
                <Form.Group controlId="formEmail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </Form.Group>
                <Button variant="primary" type="submit" className="btn-block">Submit</Button>
              </Form>
              <div className="text-center mt-3">
                <p>Remembered your password? <Link to="/login">Login</Link></p>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default ForgotPassword;
