import React from 'react';
import { useQuery, gql } from '@apollo/client';


const GET_CONTACT_QUERIES = gql`
  query GetContactQueries {
    contactQueries {
      id
      name
      email
      subject
      message
    }
  }
`;

const AdminQueryPage = () => {
  const { loading, error, data } = useQuery(GET_CONTACT_QUERIES);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div className="container mt-5">
      <h1>Contact Queries</h1>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Subject</th>
            <th>Message</th>
          </tr>
        </thead>
        <tbody>
          {data.contactQueries.map(query => (
            <tr key={query.id}>
              <td>{query.name}</td>
              <td>{query.email}</td>
              <td>{query.subject}</td>
              <td>{query.message}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminQueryPage;
