import React, { useState } from 'react';
import { useQuery, useMutation, gql } from '@apollo/client';

const GET_USERS_QUERY = gql`
  query GetUsers {
    users {
      id
      username
      role
      email
      phone
    }
  }
`;

const GET_ORDERS_QUERY = gql`
  query GetOrders {
    orders {
      id
      user {
        username
      }
      items
      total
      status
      createdAt
    }
  }
`;

const DELETE_USER_MUTATION = gql`
  mutation DeleteUser($id: ID!) {
    deleteUser(id: $id) {
      id
      username
    }
  }
`;

const UPDATE_PASSWORD_MUTATION = gql`
  mutation UpdatePassword($id: ID!, $newPassword: String!) {
    updatePassword(id: $id, newPassword: $newPassword) {
      id
      username
    }
  }
`;

const UPDATE_ORDER_STATUS_MUTATION = gql`
  mutation UpdateOrderStatus($orderId: ID!, $status: String!) {
    updateOrderStatus(orderId: $orderId, status: $status) {
      id
      status
    }
  }
`;

const AdminDashboard = () => {
  const { loading: usersLoading, error: usersError, data: usersData, refetch: refetchUsers } = useQuery(GET_USERS_QUERY);
  const { loading: ordersLoading, error: ordersError, data: ordersData, refetch: refetchOrders } = useQuery(GET_ORDERS_QUERY);
  const [deleteUser] = useMutation(DELETE_USER_MUTATION);
  const [updatePassword] = useMutation(UPDATE_PASSWORD_MUTATION);
  const [updateOrderStatus] = useMutation(UPDATE_ORDER_STATUS_MUTATION);
  const [selectedUser, setSelectedUser] = useState(null);
  const [newPassword, setNewPassword] = useState('');

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this user?');
    if (confirmDelete) {
      await deleteUser({ variables: { id } });
      refetchUsers();
    }
  };

  const handleUpdatePassword = async (id) => {
    await updatePassword({ variables: { id, newPassword } });
    setSelectedUser(null);
    setNewPassword('');
    refetchUsers();
  };

  const handleUpdateOrderStatus = async (orderId, status) => {
    await updateOrderStatus({ variables: { orderId, status } });
    refetchOrders();
  };

  if (usersLoading || ordersLoading) return <p>Loading...</p>;
  if (usersError) return <p>Error loading users</p>;
  if (ordersError) return <p>Error loading orders</p>;

  return (
    <div className="container mt-5">
      <h2>Admin Dashboard</h2>

      <h3>Users</h3>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Username</th>
            <th>Role</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {usersData.users.map((user) => (
            <tr key={user.id}>
              <td>{user.username}</td>
              <td>{user.role}</td>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(user.id)}
                >
                  Delete
                </button>
                <button
                  className="btn btn-warning ml-2"
                  onClick={() => setSelectedUser(user.id)}
                >
                  Update Password
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedUser && (
        <div className="mt-4">
          <h3>Update Password for User {selectedUser}</h3>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              handleUpdatePassword(selectedUser);
            }}
          >
            <div className="form-group">
              <label>New Password</label>
              <input
                type="password"
                className="form-control"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Update Password</button>
          </form>
        </div>
      )}

      <h3>Orders</h3>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>User</th>
            <th>Items</th>
            <th>Total</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {ordersData.orders.map((order) => (
            <tr key={order.id}>
              <td>{order.user.username}</td>
              <td>{order.items.join(', ')}</td>
              <td>{order.total}</td>
              <td>{order.status}</td>
              <td>{order.createdAt}</td>
              <td>
                <button
                  className="btn btn-success"
                  onClick={() => handleUpdateOrderStatus(order.id, 'Completed')}
                >
                  Mark as Completed
                </button>
                <button
                  className="btn btn-secondary ml-2"
                  onClick={() => handleUpdateOrderStatus(order.id, 'Cancelled')}
                >
                  Cancel Order
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
