import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';
import { useNavigate, useLocation, Link } from 'react-router-dom';
import { Container, Form, Button, Row, Col, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css'; // Ensure to create and import your custom CSS for animations

const LOGIN_MUTATION = gql`
  mutation Login($username: String!, $password: String!) {
    login(username: $username, password: $password) {
      id
      username
      role
    }
  }
`;

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [login, { data, loading, error }] = useMutation(LOGIN_MUTATION);
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const signupSuccess = queryParams.get('signup') === 'success';

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await login({ variables: { username, password } });
      const user = result.data.login;
      localStorage.setItem('user', JSON.stringify(user));
      if (user.role === 'Merchant') navigate('/about');
      else if (user.role === 'Admin') navigate('/');
      else navigate('/food');
    } catch (e) {
      console.error(e);
    }
  };

  return (
    <Container className="mt-5 fade-in">
      <Row className="justify-content-center">
        <Col md={6}>
          <Card className="shadow-lg">
            <Card.Body className="p-4">
              <h2 className="text-center mb-4">Login</h2>
              <div className="avatar-container text-center mb-4">
                <img
                  src="https://via.placeholder.com/100"
                  alt="Avatar"
                  className="rounded-circle avatar"
                />
              </div>
              {signupSuccess && <p className="text-success text-center">Signup successful! Please log in.</p>}
              <Form onSubmit={handleSubmit} className="form-login">
                <Form.Group controlId="formUsername">
                  <Form.Label>Username</Form.Label>
                  <Form.Control
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                  />
                </Form.Group>
                <Form.Group controlId="formPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </Form.Group>
                <Button variant="primary" type="submit" className="btn-block">Login</Button>
                {error && <p className="text-danger text-center mt-3">Error logging in: {error.message}</p>}
              </Form>
              <div className="text-center mt-3">
                <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
